import{_ as a,c as i,e as d,o as n}from"./app-DDsjqNbb.js";const r="/assets/1-1-B6ysfkSJ.webp",c="data:image/webp;base64,",l="data:image/webp;base64,",s="/assets/1-4-CKXibQ6z.webp",o="/assets/1-5-CE9Eme6x.webp",p="/assets/1-6-BhNtPTji.webp",h="data:image/webp;base64,",g="/assets/1-8--QAHU8MS.webp",m="/assets/1-9-CKTDKklP.webp",u="/assets/1-10-DK1g4Lpl.webp",t="/assets/1-11-D12o7cYG.webp",x="/assets/1-12-CpqztZaL.webp",f="/assets/1-13-Cq-QldGy.webp",y={};function b(A,e){return n(),i("div",null,e[0]||(e[0]=[d('<blockquote><p>消息队列MQ</p></blockquote><p><code>消息队列</code>（Message Queue，简称MQ）是一种用于在软件系统之间传递消息的通信方式。</p><p>如下图所示：</p><figure><img src="'+r+'" alt="img" tabindex="0" loading="lazy" width="964" height="175"><figcaption>img</figcaption></figure><p>消息通常包含了：要传递的信息，可以是<code>文本</code>、<code>二进制数据</code>、<code>JSON</code>、<code>XML</code>等格式。</p><p>可以是任何形式的信息，例如：订单信息、日志记录、事件等，都可以进行传输。</p><h2 id="mq消息队列应用" tabindex="-1"><a class="header-anchor" href="#mq消息队列应用"><span>MQ消息队列应用</span></a></h2><ol><li><code>解耦性</code>: 发送积分、发短信等任务，可以通过消息队列实现解耦，可以极大提升性能。</li><li><code>异步通信</code>: 在需要后台处理的任务中，发送方可以将任务信息发送到队列，然后继续执行其他操作，而不必等待任务的实时处理。</li><li><code>日志</code>: Kafka等消息队列系统常被用于日志收集，实现大规模、实时的日志处理和分析。日志场景，包含：日志的集中存储、分析和监控等等，Kafka就是典型的使用者。</li><li><code>削峰</code>: 消息队列可以平滑处理流量峰值，防止系统过载。在大型购物活动，如阿里双11期间，消息队列用于平滑处理大量的交易请求，确保系统的可伸缩性和稳定性。</li></ol><h2 id="kafka、activemq、rabbitmq、rocketmq-有什么优缺点" tabindex="-1"><a class="header-anchor" href="#kafka、activemq、rabbitmq、rocketmq-有什么优缺点"><span>Kafka、ActiveMQ、RabbitMQ、RocketMQ 有什么优缺点？</span></a></h2><table><thead><tr><th style="text-align:center;">特性</th><th style="text-align:center;"><a href="#activemq">ActiveMQ</a></th><th style="text-align:center;"><a href="#rabbitmq">RabbitMQ</a></th><th style="text-align:center;"><a href="#rocketmq">RocketMQ</a></th><th style="text-align:center;"><a href="#kafka">Kafka</a></th></tr></thead><tbody><tr><td style="text-align:center;">单机吞吐量</td><td style="text-align:center;">万级，比 RocketMQ、Kafka 低一个数量级</td><td style="text-align:center;">同 ActiveMQ</td><td style="text-align:center;">10 万级，支撑高吞吐</td><td style="text-align:center;">10 万级，高吞吐，一般配合大数据类的系统来进行实时数据计算、日志采集等场景</td></tr><tr><td style="text-align:center;">topic 数量对吞吐量的影响</td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;">topic 可以达到几百/几千的级别，吞吐量会有较小幅度的下降，这是 RocketMQ 的一大优势，在同等机器下，可以支撑大量的 topic</td><td style="text-align:center;">topic 从几十到几百个时候，吞吐量会大幅度下降，在同等机器下，Kafka 尽量保证 topic 数量不要过多，如果要支撑大规模的 topic，需要增加更多的机器资源</td></tr><tr><td style="text-align:center;">时效性</td><td style="text-align:center;">ms 级</td><td style="text-align:center;">微秒级，这是 RabbitMQ 的一大特点，延迟最低</td><td style="text-align:center;">ms 级</td><td style="text-align:center;">延迟在 ms 级以内</td></tr><tr><td style="text-align:center;">可用性</td><td style="text-align:center;">高，基于主从架构实现高可用</td><td style="text-align:center;">同 ActiveMQ</td><td style="text-align:center;">非常高，分布式架构</td><td style="text-align:center;">非常高，分布式，一个数据多个副本，少数机器宕机，不会丢失数据，不会导致不可用</td></tr><tr><td style="text-align:center;">消息可靠性</td><td style="text-align:center;">有较低的概率丢失数据</td><td style="text-align:center;">基本不丢</td><td style="text-align:center;">经过参数优化配置，可以做到 0 丢失</td><td style="text-align:center;">同 RocketMQ</td></tr><tr><td style="text-align:center;">功能支持</td><td style="text-align:center;">MQ 领域的功能极其完备</td><td style="text-align:center;">基于 erlang 开发，并发能力很强，性能极好，延时很低</td><td style="text-align:center;">MQ 功能较为完善，还是分布式的，扩展性好</td><td style="text-align:center;">功能较为简单，主要支持简单的 MQ 功能，在大数据领域的实时计算以及日志采集被大规模使用</td></tr></tbody></table><h2 id="_5大常用消息队列mq" tabindex="-1"><a class="header-anchor" href="#_5大常用消息队列mq"><span>5大常用消息队列MQ</span></a></h2><p><a id="activemq"></a></p><h3 id="_1-activemq" tabindex="-1"><a class="header-anchor" href="#_1-activemq"><span>1. ActiveMQ</span></a></h3><blockquote><p>ActiveMQ是Apache基金会的一个开源消息中间件，用于在分布式系统中实现消息传递。</p></blockquote><figure><img src="'+c+'" alt="img" tabindex="0" loading="lazy" width="311" height="100"><figcaption>img</figcaption></figure><h6 id="特点" tabindex="-1"><a class="header-anchor" href="#特点"><span>特点：</span></a></h6><p>支持JMS规范，实现了Java语言的消息中间件。</p><p>提供<code>点对点（P2P）</code>和<code>发布-订阅（Pub/Sub）</code>两种消息传递模型。</p><p>提供高级特性，如事务、持久性、消息过滤、消息导向等。</p><p>多语言支持，适用于Java、C++, .NET等。</p><h6 id="应用" tabindex="-1"><a class="header-anchor" href="#应用"><span>应用:</span></a></h6><p>适用于Java环境下的分布式系统，企业级应用中的消息传递和异步通信。</p><h6 id="性能" tabindex="-1"><a class="header-anchor" href="#性能"><span>性能:</span></a></h6><p>性能在万级/秒。</p><p><a id="rabbitmq"></a></p><h3 id="_2-rabbitmq" tabindex="-1"><a class="header-anchor" href="#_2-rabbitmq"><span>2. RabbitMQ</span></a></h3><blockquote><p>RabbitMQ是Erlang语言开发的开源消息队列服务器，支持多种消息模式和协议。</p></blockquote><figure><img src="'+l+'" alt="img" tabindex="0" loading="lazy" width="350" height="67"><figcaption>img</figcaption></figure><h6 id="特点-1" tabindex="-1"><a class="header-anchor" href="#特点-1"><span>特点:</span></a></h6><p>实现AMQP（Advanced Message Queuing Protocol）标准。</p><p>提供灵活的消息路由和交换机模型，支持多种消息模式。</p><p>具有高可用性和可扩展性，支持集群配置。</p><p>提供<code>事务</code>和<code>持久性</code>，支持<code>消息确认机制</code>。</p><h6 id="架构" tabindex="-1"><a class="header-anchor" href="#架构"><span>架构:</span></a></h6><p>RabbitMQ采用基于代理的架构，包括<code>Exchange（交换机）</code>、<code>Queue（队列）</code>、<code>Binding（绑定）</code>等核心组件。</p><p>如下图所示：</p><figure><img src="'+s+'" alt="img" tabindex="0" loading="lazy" width="1080" height="454"><figcaption>img</figcaption></figure><h6 id="应用-1" tabindex="-1"><a class="header-anchor" href="#应用-1"><span>应用:</span></a></h6><p>适用于微服务通信、任务队列、实时通信等各种场景。</p><p><a id="kafka"></a></p><h3 id="_3-kafka" tabindex="-1"><a class="header-anchor" href="#_3-kafka"><span>3. Kafka</span></a></h3><blockquote><p>Kafka是Apache基金会的一个开源分布式消息系统，用于处理实时数据流。</p></blockquote><figure><img src="'+o+'" alt="img" tabindex="0" loading="lazy" width="975" height="291"><figcaption>:size=30%</figcaption></figure><h6 id="特点-2" tabindex="-1"><a class="header-anchor" href="#特点-2"><span>特点:</span></a></h6><ul><li>高吞吐量，适用于大规模数据流处理。</li><li>持久性存储，支持消息的持久化。</li><li>分布式设计，可扩展性强，支持水平扩展。</li><li>提供多分区和多副本的特性。</li></ul><h6 id="架构-1" tabindex="-1"><a class="header-anchor" href="#架构-1"><span>架构:</span></a></h6><p>Kafka采用分布式发布-订阅模型，包括<code>Producer（生产者）</code>、<code>Broker（代理服务器</code>）、<code>Consumer（消费者）</code>等组件。</p><p>如下图所示：</p><figure><img src="'+p+'" alt="img" tabindex="0" loading="lazy" width="905" height="538"><figcaption>img</figcaption></figure><h6 id="应用-2" tabindex="-1"><a class="header-anchor" href="#应用-2"><span>应用:</span></a></h6><p>在大数据领域和实时数据处理中得到广泛应用，处理大规模数据流的场景。</p><h6 id="性能-1" tabindex="-1"><a class="header-anchor" href="#性能-1"><span>性能：</span></a></h6><p>具有极高的吞吐量，能够每秒处理数百万条消息，适用于高频率的数据流处理。</p><p><a id="rocketmq"></a></p><h3 id="_4-rocketmq" tabindex="-1"><a class="header-anchor" href="#_4-rocketmq"><span>4.RocketMQ</span></a></h3><blockquote><p>RocketMQ是阿里开源的开源消息队列，基于Paxos协议实现高可用，支持事务消息和消息轨迹。</p></blockquote><figure><img src="'+h+'" alt="img" tabindex="0" loading="lazy" width="200" height="200"><figcaption>img</figcaption></figure><h6 id="特点-3" tabindex="-1"><a class="header-anchor" href="#特点-3"><span>特点:</span></a></h6><ul><li>阿里巴巴开发，支持分布式、高可用性和可伸缩性。</li><li>提供顺序消息、事务消息等高级特性。</li><li>高度可定制化，支持分布式事务。</li></ul><h6 id="架构-2" tabindex="-1"><a class="header-anchor" href="#架构-2"><span>架构:</span></a></h6><p>RocketMQ包括<code>Namesrv（名称服务）</code>、<code>Broker（代理服务器）</code>、<code>Producer（生产者）</code>、<code>Consumer（消费者）</code>等组件。</p><p>如下图所示：</p><figure><img src="'+g+'" alt="img" tabindex="0" loading="lazy" width="640" height="321"><figcaption>img</figcaption></figure><h6 id="性能-2" tabindex="-1"><a class="header-anchor" href="#性能-2"><span>性能：</span></a></h6><p>能够处理十万级别的消息传递，支持大规模分布式部署。</p><h3 id="_5-pulsar" tabindex="-1"><a class="header-anchor" href="#_5-pulsar"><span>5.Pulsar</span></a></h3><blockquote><p>Pulsar是Apache基金会的一个开源消息队列，支持流式数据处理。</p></blockquote><figure><img src="'+m+'" alt="img" tabindex="0" loading="lazy" width="804" height="174"><figcaption>:size=30%</figcaption></figure><h6 id="特点-4" tabindex="-1"><a class="header-anchor" href="#特点-4"><span>特点:</span></a></h6><p>云原生设计，支持分布式、多租户和可伸缩。</p><p>提供水平扩展，支持多数据中心部署。</p><p>支持多种消息传递模型和协议。</p><p>提供强大的流式处理和事件驱动支持。</p><h6 id="架构-3" tabindex="-1"><a class="header-anchor" href="#架构-3"><span>架构:</span></a></h6><p>Pulsar的架构包括<code>Broker（代理服务器）</code>、<code>ZooKeeper</code>等组件。</p><figure><img src="'+u+'" alt="img" tabindex="0" loading="lazy" width="640" height="416"><figcaption>img</figcaption></figure><h6 id="应用-3" tabindex="-1"><a class="header-anchor" href="#应用-3"><span>应用:</span></a></h6><p>适用于新一代的云原生分布式消息流平台，特别适用于处理大规模数据流的场景。</p><h2 id="消息队列mq架构" tabindex="-1"><a class="header-anchor" href="#消息队列mq架构"><span>消息队列MQ架构</span></a></h2><h3 id="一-整体架构" tabindex="-1"><a class="header-anchor" href="#一-整体架构"><span>一.整体架构</span></a></h3><p>消息队列MQ的整体架构，如下图所示：</p><figure><img src="'+t+'" alt="img" tabindex="0" loading="lazy" width="756" height="143"><figcaption>img</figcaption></figure><p>主要会包含：生产者Producer、Broker、消费Consumer。</p><ul><li><h5 id="_1-生产者producer" tabindex="-1"><a class="header-anchor" href="#_1-生产者producer"><span>1.生产者Producer</span></a></h5></li></ul><p>生产者：是系统中负责创建，并发送消息到消息队列的组件。</p><p>生产者的主要责任包括：</p><ul><li><p><code>创建消息</code>：构造消息体并添加必要的元数据，例如：消息标识、时间戳等。</p></li><li><p><code>将消息发送到消息队列</code>：将消息发送到指定的队列或主题，将消息传递给中间件。</p></li><li><h5 id="_2-消息体" tabindex="-1"><a class="header-anchor" href="#_2-消息体"><span>2.消息体</span></a></h5></li></ul><p>消息体是消息中的实际数据，它包含了：需要在生产者和消费者之间传递的信息。</p><p>消息体的内容可以是：文本、JSON、二进制数据等，具体格式取决于应用的需求。</p><ul><li><h5 id="_3-broker" tabindex="-1"><a class="header-anchor" href="#_3-broker"><span>3.Broker</span></a></h5></li></ul><p>Broker中间件：<code>负责接收</code>、<code>存储</code>和<code>传递消息</code>。</p><p>如下图所示：</p><figure><img src="'+t+'" alt="img" tabindex="0" loading="lazy" width="756" height="143"><figcaption>img</figcaption></figure><p>Broker，充当了生产者和消费者之间的中介。</p><p>主要职责包括：</p><ol><li><code>接收消息</code>: 从生产者接收消息，并将其存储在消息队列中。</li><li><code>管理消息</code>: 维护消息队列的元数据，确保消息按照规定的顺序传递。</li><li><code>将消息传递给消费者</code>: 将消息传递给，订阅了相关队列或主题的消费者</li></ol><p>总之，Broker是消息队列（Message Queue）中的一个重要组件，它充当了消息的中间人，负责<code>协调</code>和<code>管理消息的传递</code>。</p><ul><li><h5 id="_4-消费者consumer" tabindex="-1"><a class="header-anchor" href="#_4-消费者consumer"><span>4.消费者Consumer</span></a></h5></li></ul><p>消费者（Consumer）是消息队列系统中的一个重要组件，其主要责任是订阅消息队列中的消息，并进行处理。</p><p>以下是一些常见的消息队列中的消费方式：</p><ol><li><code>Push模式</code>:在Push模式中，消息队列主动将消息推送给消费者。一旦消息队列接收到消息，它立即将消息推送给已经订阅了该队列或主题的消费者， 这样的方式使得消费者能够即时地获取消息。</li><li><code>Pull模式</code>:在Pull模式中，消费者主动从消息队列中拉取消息。，消费者根据需要向消息队列请求消息，然后进行处理。</li><li><code>批量拉取</code>:一些消息队列系统支持批量拉取消息，即消费者可以一次性获取多个消息。</li><li><code>定时拉取</code>:消费者可以定时地按照一定的频率拉取消息，而不是实时处理，这种方式适用于一些场景，例如：需要按照固定间隔处理消息的情况。</li></ol><h3 id="二、消息队列mq类型" tabindex="-1"><a class="header-anchor" href="#二、消息队列mq类型"><span>二、消息队列MQ类型</span></a></h3><p>消息队列类型，主要会包含：<code>点对点模型</code>和<code>订阅-发布模型</code>。</p><ul><li><h5 id="_1-点对点模型" tabindex="-1"><a class="header-anchor" href="#_1-点对点模型"><span>1.点对点模型</span></a></h5></li></ul><p>点对点模型，是一对一关系，如下图所示：</p><figure><img src="'+x+'" alt="img" tabindex="0" loading="lazy" width="693" height="162"><figcaption>img</figcaption></figure><p>在点对点模型中，<code>一条消息</code>只会被<code>一个消费</code>者消费， 即使有多个消费者订阅了同一个队列，每条消息也只会被其中一个消费者接收。</p><p>适用于<code>任务分发模式</code>，每个任务只被一个消费者执行的场景，这种模型常见于一些<code>任务分配</code>、<code>请求-响应</code>等场景。</p><ul><li><h5 id="_2-订阅-发布模型" tabindex="-1"><a class="header-anchor" href="#_2-订阅-发布模型"><span>2.订阅-发布模型</span></a></h5></li></ul><p>订阅-发布模型，全称是Publish-Subscribe Model，是消息队列中一种常见的消息传递模型，也被称为<code>发布订阅模型</code>。</p><p>如下图所示：</p><figure><img src="'+f+'" alt="img" tabindex="0" loading="lazy" width="694" height="386"><figcaption>img</figcaption></figure><h6 id="特点-5" tabindex="-1"><a class="header-anchor" href="#特点-5"><span>特点：</span></a></h6><ul><li><code>一对多关系</code>： 每条消息可以被多个消费者接收。</li><li><code>主题（Topic）</code>： 消息被发布到一个主题，而订阅者可以选择订阅特定的主题。</li><li><code>发布者（Publisher）</code>： 生产者或发布者，负责将消息发布到一个或多个主题。</li><li><code>订阅者（Subscriber）</code>： 消费者或订阅者，可以选择订阅一个或多个主题，以接收发布到这些主题的消息。</li></ul><h6 id="流程" tabindex="-1"><a class="header-anchor" href="#流程"><span>流程：</span></a></h6><ul><li><code>发布消息：</code> 发布者将消息发布到一个特定的主题，如&quot;新闻&quot;或&quot;实时通知&quot;。</li><li><code>订阅主题：</code> 订阅者，选择订阅感兴趣的主题，比如：订阅&quot;新闻&quot;主题。</li><li><code>消息传递：</code> 当发布者发布与订阅者已订阅的主题相关的消息时，订阅者将接收到这些消息。</li></ul><h6 id="应用场景" tabindex="-1"><a class="header-anchor" href="#应用场景"><span>应用场景：</span></a></h6><ul><li><p><code>实时广播</code>： 适用于需要将消息广播给多个消费者的场景，例如：实时通知、广播消息等。</p></li><li><p><code>事件驱动</code>： 用于实现事件驱动架构。</p></li><li><p><code>分布式系统集成</code>： 在分布式系统中，发布-订阅模型可以用于实现组件之间的松耦合通信，提高系统的灵活性和可扩展性。</p></li><li><h5 id="_3-点对点和发布订阅的区别" tabindex="-1"><a class="header-anchor" href="#_3-点对点和发布订阅的区别"><span>3.点对点和发布订阅的区别</span></a></h5></li></ul><p><code>点对点模型</code>适用于<code>一对一</code>的场景，关注直接连接的点对点关系，而发布订阅模型适用于一对多的场景。</p><h6 id="点对点模型" tabindex="-1"><a class="header-anchor" href="#点对点模型"><span>点对点模型</span></a></h6><p>着重于消息的直接传递，消息的生产者和消费者之间直接连接。</p><p>这种模型通常强调点对点的关系，消息的生产者和消费者之间是一对一的。</p><h6 id="发布订阅模型" tabindex="-1"><a class="header-anchor" href="#发布订阅模型"><span>发布订阅模型</span></a></h6><p>着重于消息的广播和分发，消息的发布者和订阅者之间通过主题进行间接连接。</p><p>这种模型通常强调一对多的关系，一个消息可以被多个订阅者接收。</p><h2 id="名词解释" tabindex="-1"><a class="header-anchor" href="#名词解释"><span>名词解释</span></a></h2><blockquote><p>主要对 MQ 涉及的专有名词及术语进行定义和解析，方便您更好地理解相关概念并使用 MQ。</p></blockquote><table><thead><tr><th style="text-align:center;">名词</th><th style="text-align:center;">中文名称</th><th style="text-align:center;">描述</th></tr></thead><tbody><tr><td style="text-align:center;">Message Queue</td><td style="text-align:center;">消息队列</td><td style="text-align:center;">消息队列，专业消息中间件，是企业级互联网架构的核心产品，提供基于高可用分布式集群技术搭建的消息发布订阅、轨迹查询、资源统计、定时（延时）、监控报警等一系列消息云服务。</td></tr><tr><td style="text-align:center;">Message</td><td style="text-align:center;">消息</td><td style="text-align:center;">消息队列中的信息传递载体。</td></tr><tr><td style="text-align:center;">Message ID</td><td style="text-align:center;">消息ID</td><td style="text-align:center;">由 MQ 系统自动生成，唯一标识某条消息。</td></tr><tr><td style="text-align:center;">Message Key</td><td style="text-align:center;">消息Key</td><td style="text-align:center;">由消息生产者（Producer）设置，唯一标识某个消息。</td></tr><tr><td style="text-align:center;"><code>Topic</code></td><td style="text-align:center;">主题</td><td style="text-align:center;">消息队列中的消息分类，由消息生产者（Producer）设置，由消息消费者（Consumer）订阅。</td></tr><tr><td style="text-align:center;"><code>Tag</code></td><td style="text-align:center;">标签</td><td style="text-align:center;">消息标签，二级消息类型，用来进一步区分某个 Topic 下的消息分类。</td></tr><tr><td style="text-align:center;"><code>Producer</code></td><td style="text-align:center;">消息生产者</td><td style="text-align:center;">消息生产者，也称为消息发布者，负责生产并发送消息。</td></tr><tr><td style="text-align:center;">Producer ID</td><td style="text-align:center;">生产者ID</td><td style="text-align:center;">由消息生产者（Producer）设置，唯一标识某个 Producer。</td></tr><tr><td style="text-align:center;">Producer 实例</td><td style="text-align:center;">生产者实例</td><td style="text-align:center;">Producer 的一个对象实例，不同的 Producer 实例可以运行在不同进程内或者不同机器上。Producer 实例线程安全，可在同一进程内多线程之间共享。</td></tr><tr><td style="text-align:center;"><code>Consumer</code></td><td style="text-align:center;">消息消费者</td><td style="text-align:center;">消息消费者，也称为消息订阅者，负责接收并消费消息。</td></tr><tr><td style="text-align:center;">Consumer ID</td><td style="text-align:center;">消费者ID</td><td style="text-align:center;">一类 Consumer 的标识，这类 Consumer 通常接收并消费一类消息，且消费逻辑一致。</td></tr><tr><td style="text-align:center;">Consumer 实例</td><td style="text-align:center;">消费者实例</td><td style="text-align:center;">Consumer 的一个对象实例，不同的 Consumer 实例可以运行在不同进程内或者不同机器上。Consumer 实例线程安全，可在同一进程内多线程之间共享。一个 Consumer 实例内配置线程池消费消息。</td></tr><tr><td style="text-align:center;">集群消费</td><td style="text-align:center;"></td><td style="text-align:center;">一个 Consumer ID 所标识的所有 Consumer 平均分摊消费消息。例如某个 Topic 有 9 条消息，一个 Consumer ID 有 3 个 Consumer 实例，那么在集群消费模式下每个实例平均分摊，只消费其中的 3 条消息。</td></tr><tr><td style="text-align:center;">广播消费</td><td style="text-align:center;"></td><td style="text-align:center;">一个 Consumer ID 所标识的所有 Consumer 都会各自消费某条消息一次。例如某个 Topic 有 9 条消息，一个 Consumer ID 有 3 个 Consumer 实例，那么在广播消费模式下每个实例都会各自消费 9 条消息。</td></tr><tr><td style="text-align:center;">定时消息</td><td style="text-align:center;"></td><td style="text-align:center;">Producer 将消息发送到 MQ 服务端，但并不期望这条消息立马投递，而是推迟到在当前时间点之后的某一个时间投递到 Consumer 进行消费，该消息即定时消息。</td></tr><tr><td style="text-align:center;">延时消息</td><td style="text-align:center;"></td><td style="text-align:center;">Producer 将消息发送到 MQ 服务端，但并不期望这条消息立马投递，而是延迟一定时间后才投递到 Consumer 进行消费，该消息即延时消息。</td></tr><tr><td style="text-align:center;">事务消息</td><td style="text-align:center;"></td><td style="text-align:center;">MQ 提供类似 X/Open XA 的分布事务功能，通过 MQ 事务消息能达到分布式事务的最终一致。</td></tr><tr><td style="text-align:center;">顺序消息</td><td style="text-align:center;"></td><td style="text-align:center;">MQ 提供的一种按照顺序进行发布和消费的消息类型, 分为全局顺序消息和分区顺序消息。</td></tr><tr><td style="text-align:center;">顺序发布</td><td style="text-align:center;"></td><td style="text-align:center;">对于指定的一个 Topic，客户端将按照一定的先后顺序进行发送消息。</td></tr><tr><td style="text-align:center;">顺序消费</td><td style="text-align:center;"></td><td style="text-align:center;">对于指定的一个 Topic，按照一定的先后顺序进行接收消息，即先发送的消息一定会先被客户端接收到。</td></tr><tr><td style="text-align:center;">全局顺序消息</td><td style="text-align:center;"></td><td style="text-align:center;">对于指定的一个 Topic，所有消息按照严格的先入先出（FIFO）的顺序进行发布和消费。</td></tr><tr><td style="text-align:center;">分区顺序消息</td><td style="text-align:center;"></td><td style="text-align:center;">对于指定的一个 Topic，所有消息根据 sharding key 进行区块分区。同一个分区内的消息按照严格的 FIFO 顺序进行发布和消费。Sharding key 是顺序消息中用来区分不同分区的关键字段，和普通消息的 key 是完全不同的概念。</td></tr><tr><td style="text-align:center;">消息堆积</td><td style="text-align:center;"></td><td style="text-align:center;">Producer 已经将消息发送到 MQ 服务端，但由于 Consumer 消费能力有限，未能在短时间内将所有消息正确消费掉，此时在 MQ 服务端保存着未被消费的消息，该状态即消息堆积。</td></tr><tr><td style="text-align:center;">消息过滤</td><td style="text-align:center;"></td><td style="text-align:center;">订阅者可以根据消息标签（Tag）对消息进行过滤，确保订阅者最终只接收被过滤后的消息类型。消息过滤在 MQ 服务端完成。</td></tr><tr><td style="text-align:center;">消息轨迹</td><td style="text-align:center;"></td><td style="text-align:center;">在一条消息从发布者发出到订阅者消费处理过程中，由各个相关节点的时间、地点等数据汇聚而成的完整链路信息。通过消息轨迹，用户能清晰定位消息从发布者发出，经由 MQ 服务端，投递给消息订阅者的完整链路，方便定位排查问题。</td></tr><tr><td style="text-align:center;">重置消费位点</td><td style="text-align:center;"></td><td style="text-align:center;">以时间轴为坐标，在消息持久化存储的时间范围内（默认3天），重新设置消息订阅者对其订阅 Topic 的消费进度，设置完成后订阅者将接收设定时间点之后由消息发布者发送到 MQ 服务端的消息。</td></tr></tbody></table>',128)]))}const C=a(y,[["render",b]]),k=JSON.parse('{"path":"/queue/","title":"初识消息队列","lang":"zh-CN","frontmatter":{"title":"初识消息队列","createTime":"2025/07/07 16:50:22","permalink":"/queue/","description":"消息队列MQ 消息队列（Message Queue，简称MQ）是一种用于在软件系统之间传递消息的通信方式。 如下图所示： imgimg 消息通常包含了：要传递的信息，可以是文本、二进制数据、JSON、XML等格式。 可以是任何形式的信息，例如：订单信息、日志记录、事件等，都可以进行传输。 MQ消息队列应用 解耦性: 发送积分、发短信等任务，可以通过消息...","head":[["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"初识消息队列\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2025-07-07T09:12:44.000Z\\",\\"author\\":[]}"],["meta",{"property":"og:url","content":"https://docs.sreio.com/queue/"}],["meta",{"property":"og:site_name","content":"Sreio Docs"}],["meta",{"property":"og:title","content":"初识消息队列"}],["meta",{"property":"og:description","content":"消息队列MQ 消息队列（Message Queue，简称MQ）是一种用于在软件系统之间传递消息的通信方式。 如下图所示： imgimg 消息通常包含了：要传递的信息，可以是文本、二进制数据、JSON、XML等格式。 可以是任何形式的信息，例如：订单信息、日志记录、事件等，都可以进行传输。 MQ消息队列应用 解耦性: 发送积分、发短信等任务，可以通过消息..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2025-07-07T09:12:44.000Z"}],["meta",{"property":"article:modified_time","content":"2025-07-07T09:12:44.000Z"}]]},"readingTime":{"minutes":14.24,"words":4271},"git":{"createdTime":1751879564000,"updatedTime":1751879564000,"contributors":[{"name":"sreio","username":"sreio","email":"ingwei@163.com","commits":1,"avatar":"https://avatars.githubusercontent.com/sreio?v=4","url":"https://github.com/sreio"}],"changelog":[{"hash":"ec6b65d5f5e156de0bddee87610673d45a5591df","time":1751879564000,"email":"ingwei@163.com","author":"sreio","message":"queue"}]},"autoDesc":true,"filePathRelative":"queue/1.readme.md","headers":[]}');export{C as comp,k as data};
